import React, { useState, useEffect } from 'react';

const Scheduler = ({ players }) => {
    const [selectedPlayerIndex, setSelectedPlayerIndex] = useState(0);

    useEffect(() => {
        // Load the last selected player index from local storage on component mount
        const lastIndex = localStorage.getItem('lastSelectedPlayerIndex');
        if (lastIndex !== null) {
            setSelectedPlayerIndex(parseInt(lastIndex, 10));
        }
    }, []);

    useEffect(() => {
        // Save the current selected player index to local storage whenever it changes
        localStorage.setItem('lastSelectedPlayerIndex', selectedPlayerIndex);
    }, [selectedPlayerIndex]);

    const selectNextPlayer = () => {
        setSelectedPlayerIndex((prevIndex) => (prevIndex + 1) % players.length);
    };

    return (
        <div>
            <h1>Tennis Scheduler</h1>
            <p>Current Player: {players[selectedPlayerIndex]}</p>
            <button onClick={selectNextPlayer}>Select Next Player</button>
        </div>
    );
};

export default Scheduler;
